import React, { useState } from 'react';

export default function Transfer() {
  const [formData, setFormData] = useState({
    senderAccount: ""
  })

  function handleChange (event) {
    setFormData(prevFormData => {
        return {
            ...prevFormData,
            [event.target.name]: event.target.value
        }
    })
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(formData);
    setIsSubmitted(true);
  }

  const [isSubmitted, setIsSubmitted] = useState(false);
  const closeModal = () => {
    setIsSubmitted(false);
  };


  return (
    <div className='transfer'>
        <div className='transfer-header'>
                <p>Move Money</p>
        </div>
        <div className='transfer-section'>
            <div className='transfer-section-sidebar'>

                <div className='transfer-section-sidebar-first-elem'>
                    <p >Pay or Transfer</p>
                </div>
                <div className='transfer-section-sidebar-second-elem'>
                    <p >Scheduled Payments</p>
                </div>
                <div className='transfer-section-sidebar-third-elem'>
                    <p >View Scheduled Bills</p>
                </div>
            </div>
            <div className='transfer-section-content'>
                <form onSubmit={handleSubmit}>
                    <p className='transfer-section-content-header'>From:</p>
                    <span>
                        <label htmlFor='sender-account' className='transfer-section-content-sub-header'>Account</label>
                        <select
                            id='sender-account'
                            name='senderAccount'
                            value={formData.senderAccount}
                            onChange={handleChange}
                        >
                            <option value="">-- Choose Account --</option>
                            <option value="063786 48543425213">Transaction Account: 063786 48543425213</option>
                            <option value="063123 4857252935">Savings Account: 063123 4857252935</option>
                        </select>
                    </span>
                    <p className='transfer-section-content-header'>To:</p>
                    <p className='transfer-section-content-sub-header'>Transfer Options</p>
                    <div className='transfer-section-payer-details'>
                        <p>Pay Option Display</p>
                    </div>
                    <p className='transfer-section-content-header'>Payment Details:</p>
                    <p className='transfer-section-content-sub-header'>Amount</p>
                    <p className='transfer-section-content-sub-header'>Description (Optional)</p>
                    <div className='transfer-section-button-wrapper'>
                        <button type='submit'>Submit</button>
                    </div>
                </form>
            </div>
            {isSubmitted && (
                <div className="modal-overlay">
                <div className="modal-content">
                    <p>Payment sent successfully!</p>
                    <button onClick={closeModal}>Close</button>
                </div>
                </div>
            )}
        </div>
    </div>
  )
}
