import React, { useState } from 'react';
import addSign from '../Assets/add-sign.svg';
export default function Contacts() {
  const accounts = [
    { name: "Jackson Williams", bsb: 95328, accountNum: 88293680 },
    { name: "Jackson Johnson", bsb: 46086, accountNum: 84140165 },
    { name: "Jackson Smith", bsb: 79310, accountNum: 82949178 },
    { name: "Jackson Jones", bsb: 23592, accountNum: 88960684 },
    { name: "Jackson Smith", bsb: 85468, accountNum: 43667333 },
    { name: "Jackson Williams", bsb: 55418, accountNum: 70599594 },
    { name: "Jackson Jones", bsb: 99360, accountNum: 65792614 },
    { name: "Jackson Jones", bsb: 18712, accountNum: 85752163 },
    { name: "Jackson Brown", bsb: 46198, accountNum: 27744551 },
    { name: "Jackson Williams", bsb: 32604, accountNum: 65487411 }
  ];
  const phones = [
    { name: "Jackson Williams", phoneNum: "0274893621" },
    { name: "Jackson Johnson", phoneNum: "0412758390" },
    { name: "Jackson Smith", phoneNum: "0487261538" },
    { name: "Jackson Jones", phoneNum: "0354918276" },
    { name: "Jackson Brown", phoneNum: "0192837465" },
    { name: "Jackson White", phoneNum: "0678123456" },
    { name: "Jackson Taylor", phoneNum: "0743658912" },
    { name: "Jackson Davis", phoneNum: "0283746591" },
    { name: "Jackson Miller", phoneNum: "0365289174" },
    { name: "Jackson Wilson", phoneNum: "0456789123" }
  ];

  const [active, setActive] = useState('bank');
  function onClickDiv(type) {
    setActive(type);
  }
  const [isAdd, setIsAdd] = useState(false);
  const [contactData, setContactData] = useState({
    contactType:"",
    name:"",
    bsb:"",
    accountNum:"",
    phoneNum:""
  });
  function handleChange (event) {
    const {name, value} = event.target;
    setContactData(
      prevContactData => {
        return {
          ...prevContactData,
          [name]:value
        }
      }
    );
  };
  function handleSubmit(event) {
    event.preventDefault();
    console.log(contactData)
  }
  return (
    <div className="contact">
      <div className="contact-header">
        <p className='contact-header-left'>Contact List</p>
        <img src= {addSign} alt = '' onClick={() => setIsAdd(true)}/>
      </div>
      <div className="contact-container">
        <div className="contact-sidebar">
          <div className={`sidebar-bank ${active === "bank" ? "sidebar-bank-active" : ""}`} onClick={() => onClickDiv("bank")}>
            <p>Bank account</p>
          </div>
          <div className={`sidebar-phone ${active === "phone" ? "sidebar-phone-active" : ""}`} onClick={() => onClickDiv("phone")}>
            <p>Phone</p>
          </div>
        </div>
        <div className="contact-content">
          {active === "bank" && (
            <>
            {accounts.length> 0 && (
              <>
              {accounts.map((account) =>{
                return <div>
                  <h3> {account.name} </h3>
                  <p> BSB: {account.bsb} </p>
                  <p> Account Number: {account.accountNum}</p>
                </div>
              })}
              </>
            )}
            {accounts.length === 0 && (
              <>
              No Contacts!
              </>
            )}
            </>
          )}
          {active === "phone" && (
            <>
            {phones.length> 0 && (
              <>
              {phones.map((phone) => {
                  return <div>
                    <h3> {phone.name} </h3>
                    <p> {phone.phoneNum} </p>
                  </div>
              })}
              </>
            )}
            {phones.length === 0 && (
              <>
              No Contacts!
              </>
            )}
            </>
          )}
        </div>
      </div>
      {isAdd && (
        <>
          <div className='contact-form-overlay'>
          <div className='contact-form-content'>
            <form onSubmit={handleSubmit}>
            <span>
                    <label htmlFor='contact-type' className='contact-type'>Transfer Options</label>
                    <select
                        id='contact-type'
                        name='contactType'
                        value={contactData.contactType}
                        onChange={handleChange}
                        className='contact-type-select'
                        required
                    >
                        <option value="">-- Choose Method --</option>
                        <option value="Bank">Bank</option>
                        <option value="Phone">Phone</option>
                    </select>
                </span>
                {contactData.contactType === "Bank" && (
                      <>
                      <span>
                          <p className='contact-name'>Name</p>
                              <input
                                  type="name"
                                  placeholder="Name"
                                  onChange={handleChange}
                                  name="name"
                                  value={contactData.name}
                                  className="contact-name-details"
                                  required
                              />
                      </span>
                      <span>
                          <p className='contact-bsb'>BSB</p>
                              <input
                                  type="number"
                                  placeholder="BSB"
                                  onChange={handleChange}
                                  name="bsb"
                                  value={contactData.bsb}
                                  className="contact-bsb-details"
                                  min={100000}  // Ensures BSB is at least 5 digits
                                  max={999999}  // Ensures BSB is at most 5 digits
                                  pattern="\d{5}"
                                  title="Please enter exactly 6 digits"
                                  required
                              />
                      </span>
                      <span>
                        <p className='contact-account-number'>Account Number</p>
                          <input
                            type="number"
                            placeholder="Account Number"
                            onChange={handleChange}
                            name="accountNum"
                            value={contactData.accountNum}
                            className="contact-account-number-details"
                            min={10000000}  // Ensures account number is at least 8 digits
                            max={99999999}  // Ensures account number is at most 8 digits
                            pattern="\d{8}"
                            title="Please enter exactly 8 digits"
                            required
                          />
                      </span>
                      </>
                  )}
                  {formData.transferMethod === "PayID" && (
                      <>
                      <span>
                        <p className='transfer-section-content-sub-header'>Phone Number</p>
                          <input
                            type="text"
                            placeholder="Phone Number"
                            onChange={handleChange}
                            name="accountNumber"
                            value={formData.phoneNumber}
                            className="transfer-receiver-details"
                            maxLength={10}
                            pattern="\d{10}"
                            title="Please enter exactly 10 digits"
                            required
                          />
                      </span>
                      </>
                  )}
                <button type='submit'>Submit</button>
            </form>
            <button onClick={() => {setIsAdd(false)}}>Close</button>
          </div>
        </div>
        </>
      )}
    </div>
  )
}
