import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default function SignUp() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phoneNo: '',
    username: '',
    password: '',
    initialDeposit: ''
  })

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const {name, value} = e.target;
    setFormData({...formData, [name]: value});
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // POST request to your backend API
      const response = await axios.post('http://localhost:5000/api/accounts/create', formData);
      setMessage(response.data.message); // Success message from backend
    } catch (error) {
      if (error.response && error.response.data) {
        setMessage(error.response.data.message); // Error message from backend
      } else {
        setMessage('An error occurred during signup');
      }
    }


  }




  return (
    <div 
        tabIndex="0" // Make div focusable
        onKeyDown={handleKeyDown} // Capture key events
        autoFocus // Automatically focus on the div
        style={{ outline: "none" }} // Remove default focus outline
    >
        <h1>List</h1>
        <input
            type="text"
            value={newRow}
            onChange={(e) => setNewRow(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Add a new row"
        />
        <button onClick={addRow}>Add Row</button>
        <div>
            {rows.map((row, index) => (
                <article
                    key={index}
                    className={`card ${animatedRows.includes(index) ? "fade-In" : ""}`}
                    onClick={() => toggleRowCompletion(index)}
                    onAnimationEnd={() => handleAnimationEnd(index)}
                    onMouseEnter={() => setHoveredRow(index)}
                    onMouseLeave={() => setHoveredRow(null)}
                    style={{
                        border: "1px solid #ddd",
                        margin: "10px",
                        padding: "15px",
                        borderRadius: "5px",
                        backgroundColor: "#f9f9f9",
                        boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
                        width: "300px",
                        overflow: "hidden",
                        borderColor: "",
                        borderWidth: "3px",
                    }}
                >
                    <h3
                        style={{
                            textDecoration: row.completed ? "line-through" : "none",
                            whiteSpace: "nowrap",
                            overflowX: "auto",
                            display: "block",
                            maxWidth: "100%",
                            scrollbarWidth: "none",
                            msOverflowStyle: "none",
                        }}
                    >
                        {row.text}
                    </h3>
                    <div>
                        <button onClick={(e) => { e.stopPropagation(); deleteRow(index); }}>Delete</button>
                    </div>
                </article>
            ))}
        </div>
    </div>
);
}
