import React, { useEffect, useCallback, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import BillHistory from '../../Pages/AccountPages/BillHistory/BillHistory';
import './ScheduledBillsTable.css';
export default function ScheduledBillsTable() {

  const [data, setData] = useState([]);
  const [view, setView] = useState(false);
  const [currId, setCurrId] = useState(-1);
  const token = localStorage.getItem('authToken');
  function onClickLink(id) {
    setCurrId(id);
    setView(true);
  }

  const getScheduledBills = useCallback(async () => {
    try {
        const response = await axios.get('http://localhost:5000/api/transactions/getScheduledPayments', {}, {
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${token}`
            }
        });

        const formattedResponse = response.data.map((bills, index) => ({
            id: index + 1,  // Create an artificial id
            name: bills.name,
            amount: bills.amount,
            date: bills.startDate,
            details: 'View details'
        }));
        console.log("FORMATTED RESPONSE: ", formattedResponse);
        setData(formattedResponse);
    } catch (error) {
        console.error('Error fetching scheduled bills: ', error);
    }
  });

  useEffect(() => {
    getScheduledBills();
}, [getScheduledBills]);

  return (
    <div className='scheduled-bills-table'>
    {!view && (
        <>
        <table className="styled-table">
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Amount</th>
                    <th>Date</th>
                    <th>Details</th>
                </tr>
                </thead>
                <tbody>
                {data.map((row) => (
                    <tr key={row.id}>
                    <td>{row.name}</td>
                    <td>{row.amount}</td>
                    <td>{row.date}</td>
                    <td>
                        <Link onClick={() => onClickLink(row.id)}>
                            {row.details}
                        </Link>
                    </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </>
    )}
    {view && (
        <>
        <BillHistory currId = {currId}/>
        <button className = 'scheduled-bill-history-button' onClick={() => setView(false)}>Back</button>
        </>
    )}
    </div>
  )
}
